    public static int searchThePeakElement(int []arr){
        int n = arr.length;
        if (n == 1) {
            return 0;
        }
        if (arr[0] > arr[1]) {
            return 1;
        }
        if (arr[n - 1] > arr[n - 2]) {
            return n-1;
        }
        int index = -1;
        for (int i = 0; i < n-2; i++) {
            if(arr[i]<arr[i+1] && arr[i+1]>arr[i+2]){
                index=i+1;
                break;
            }
        }
        return index;
    }
